# -*- coding:utf-8 -*-import requestsimport sysimport iosys.stdout = io.TextIOWrapper(sys.stdout.buffer, encoding='utf8')  # 改变标准输出的默认编码r = requests.get("https://www.codedraw.cn")print(r.status_code)  # 检查Response对象的返回值 200表示访问成功 400/其他 失败print(r.raise_for_status())print(r.encoding)  # 首选的 从HTTP Header中猜测的编码方式# 如果header 中不存在charset 字段 则默认为ISO-8859-1 编码print(r.apparent_encoding)  # 备选的编码方式  根据网页的内容分析编码# print(r.text)  # 输出网页源代码